import psycopg2
import redis

if not DEBUG:
    print("ğŸ§ª [PRODUCTION MODE] Checking container-based PostgreSQL and Redis connections...")

    # Parse DATABASE_URL
    db_url = os.getenv("DATABASE_URL")
    if db_url:
        try:
            parsed = urlparse(db_url)
            conn = psycopg2.connect(
                dbname=parsed.path.lstrip("/"),
                user=parsed.username,
                password=parsed.password,
                host=parsed.hostname,
                port=parsed.port,
            )
            print("âœ… Successfully connected to PostgreSQL via DATABASE_URL!")
            conn.close()
        except Exception as e:
            print(f"âŒ PostgreSQL Connection Error via DATABASE_URL: {e}")
