#                 LLM_POOL.append(llm)


def initialize_llm_pool():
    global LLM_POOL
    with LLM_POOL_LOCK:
        if not LLM_POOL:
            for key in OPENAI_KEYS:
                llm = ChatOpenAI(
                    openai_api_key=key,
                    model="gpt-4o",
                    temperature=0,
                )
                LLM_POOL.append(llm)


def get_llm_instance():
    if not LLM_POOL:
        initialize_llm_pool()
    return choice(LLM_POOL)


